generated.css:
@import url("https://fonts.googleapis.com/css?family=Open+Sans|Poppins:900");

html {
  font-size: 125%;
  font-family: "Open Sans";
  line-height: 1.5;
}

body {
  display: grid;
  grid-template-columns: 10vw auto 22vw;
  grid-template-rows: 25% 100px auto;
  margin: 0;
  padding: 0;
  width: 100%;
  max-width: 100vw;
}

h1 {
  font-family: "Poppins";
  line-height: 0.7;
}

h1.article-title {
    font-size: 20vh;
    width: 50%;
  }

main {
  padding: 0 2em;
  border-left: 1px solid #f2efea;
}

.news {
  background-color: #f3f6fb;
  font-size: 80%;
}

.news__weather {
    background-color: #4cc4fb;
    padding-bottom: 0.7em;
  }

.news__weather > * {
      padding: 0.75em 1.25em;
    }

.news__weather.wind {
      background-color: #ccefff;
    }

.news__weather.wind header {
        background-color: #7fd8f7;
      }

.news__weather.rain {
      background-color: #4cc4fb;
    }

.news__weather.rain header {
        background-color: #03a9f4;
      }

.news__weather header {
      background-color: #03a9f4;
      color: white;
    }

.news__articles {
    padding: 1em;
  }

.sidenavigation {
  position: relative;
}

.sidenavigation__items {
    position: fixed;
    top: 6vh;
    padding: 0;
  }

.sidenavigation__item {
    padding: 1em;
    list-style-type: none;
    max-width: 80%;
  }

.sidenavigation__item--active {
      width: 61%;
      padding: 1em 1em 0.7em;
      background-color: black;
      color: white;
    }
